import React from 'react';

class Posts extends React.Component {
  // The constructor initializes the component's state with an empty array for posts. [cite: 48]
  constructor(props) { //
    super(props); //
    this.state = {
      posts: [],
      error: null
    };
  }

  // This method uses the Fetch API to get post data from the specified URL. [cite: 49, 50]
  loadPosts() {
    fetch('https://jsonplaceholder.typicode.com/posts') //
      .then(response => response.json())
      .then(data => {
        // Once data is fetched, it updates the component's state.
        this.setState({ posts: data });
      })
      .catch(error => {
        this.setState({ error: error });
      });
  }

  // componentDidMount() is a lifecycle hook that runs after the component has been rendered to the DOM.
  // We call loadPosts() here to fetch the data. [cite: 52]
  componentDidMount() { //
    this.loadPosts(); //
  }

  // componentDidCatch() is used to catch errors in any child components.
  // It will display an alert message if an error occurs. [cite: 56]
  componentDidCatch(error, info) { //
    alert('An error occurred: ' + error.message); //
  }

  // The render() method displays the content. [cite: 54]
  render() { //
    const { posts, error } = this.state;

    // Display an error message if the fetch failed.
    if (error) {
      return <div>Error: {error.message}</div>;
    }

    // Display a loading message while posts are being fetched.
    if (posts.length === 0) {
      return <div>Loading posts...</div>;
    }

    // Map through the posts array and display the title and body for each post. [cite: 54]
    return (
      <div>
        <h1>Blog Posts</h1>
        {posts.map(post => (
          <div key={post.id} style={{ borderBottom: '1px solid #ccc', marginBottom: '20px', paddingBottom: '20px' }}>
            <h2>{post.title}</h2>
            <p>{post.body}</p>
          </div>
        ))}
      </div>
    );
  }
}

export default Posts;
